        [Fact]
        public void LogarithmicArithmeticOperators()
        {
            LevelToCarrier v = LevelToCarrier.FromDecibelsCarrier(40);
            AssertEx.EqualTolerance(-40, -v.DecibelsCarrier, DecibelsCarrierTolerance);
            AssertLogarithmicAddition();
            AssertLogarithmicSubtraction();
            AssertEx.EqualTolerance(50, (v*10).DecibelsCarrier, DecibelsCarrierTolerance);
            AssertEx.EqualTolerance(50, (10*v).DecibelsCarrier, DecibelsCarrierTolerance);
            AssertEx.EqualTolerance(35, (v/5).DecibelsCarrier, DecibelsCarrierTolerance);
            AssertEx.EqualTolerance(35, v/LevelToCarrier.FromDecibelsCarrier(5), DecibelsCarrierTolerance);
        }

        protected abstract void AssertLogarithmicAddition();

        protected abstract void AssertLogarithmicSubtraction();

        [Fact]
        public void ComparisonOperators()
        {
            LevelToCarrier oneDecibelCarrier = LevelToCarrier.FromDecibelsCarrier(1);
            LevelToCarrier twoDecibelsCarrier = LevelToCarrier.FromDecibelsCarrier(2);

            Assert.True(oneDecibelCarrier < twoDecibelsCarrier);
            Assert.True(oneDecibelCarrier <= twoDecibelsCarrier);
            Assert.True(twoDecibelsCarrier > oneDecibelCarrier);
            Assert.True(twoDecibelsCarrier >= oneDecibelCarrier);

            Assert.False(oneDecibelCarrier > twoDecibelsCarrier);
            Assert.False(oneDecibelCarrier >= twoDecibelsCarrier);
            Assert.False(twoDecibelsCarrier < oneDecibelCarrier);
            Assert.False(twoDecibelsCarrier <= oneDecibelCarrier);
        }

