        [Theory]
        [InlineData(1, LevelToCarrierUnit.DecibelCarrier, 1, LevelToCarrierUnit.DecibelCarrier, true)]  // Same value and unit.
        [InlineData(1, LevelToCarrierUnit.DecibelCarrier, 2, LevelToCarrierUnit.DecibelCarrier, false)] // Different value.
        [InlineData(2, LevelToCarrierUnit.DecibelCarrier, 1, LevelToCarrierUnit.DecibelCarrier, false)] // Different value and unit.
        public void Equals_ReturnsTrue_IfValueAndUnitAreEqual(double valueA, LevelToCarrierUnit unitA, double valueB, LevelToCarrierUnit unitB, bool expectEqual)
        {
            var a = new LevelToCarrier(valueA, unitA);
            var b = new LevelToCarrier(valueB, unitB);

            // Operator overloads.
            Assert.Equal(expectEqual, a == b);
            Assert.Equal(expectEqual, b == a);
            Assert.Equal(!expectEqual, a != b);
            Assert.Equal(!expectEqual, b != a);

            // IEquatable<T>
            Assert.Equal(expectEqual, a.Equals(b));
            Assert.Equal(expectEqual, b.Equals(a));

            // IEquatable
            Assert.Equal(expectEqual, a.Equals((object)b));
            Assert.Equal(expectEqual, b.Equals((object)a));
        }

        [Fact]
