        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentCulture;
            try
            {
                CultureInfo.CurrentCulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 dB", new LevelToCarrier(0.123456, LevelToCarrierUnit.DecibelCarrier).ToString("s1"));
                Assert.Equal("0.12 dB", new LevelToCarrier(0.123456, LevelToCarrierUnit.DecibelCarrier).ToString("s2"));
                Assert.Equal("0.123 dB", new LevelToCarrier(0.123456, LevelToCarrierUnit.DecibelCarrier).ToString("s3"));
                Assert.Equal("0.1235 dB", new LevelToCarrier(0.123456, LevelToCarrierUnit.DecibelCarrier).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentCulture = oldCulture;
            }
        }

        [Fact]
